(20526, 887, 'about_partners', 'a:2:{i:0;s:3:\"882\";i:1;s:3:\"883\";}'),
(20527, 887, '_about_partners', 'field_5b97a05b00885'),
(20528, 887, 'about_partners_0_image', '139'),
(20529, 887, '_about_partners_0_image', 'field_5b97a34d4685b'),
(20530, 887, 'about_partners_0_description', 'As the first authorised SI for the country, GBM will offer a range of services and hardware to deploy Apple solutions, with a focus on integrated mobility solutions. GBM will provide dedicated Apple business strategy, consultancy, as well as implementation services.\r\n\r\nPhilippe Jarre, chief executive officer of GBM, stated: \"GBM has a 26-year heritage of partnering with leaders in the global IT industry. As we continue our transformation journey at GBM, our collaboration with Apple is a commitment to bringing further innovations to our customers, delivering more value to our stakeholders and enriching the market with a new set of solutions.\"'),
(20531, 887, '_about_partners_0_description', 'field_5b97a3674685c'),
(20532, 887, 'about_partners_1_image', '140'),
(20533, 887, '_about_partners_1_image', 'field_5b97a34d4685b'),
(20534, 887, 'about_partners_1_description', 'As the first authorised SI for the country, GBM will offer a range of services and hardware to deploy Apple solutions, with a focus on integrated mobility solutions. GBM will provide dedicated Apple business strategy, consultancy, as well as implementation services.\r\n\r\nPhilippe Jarre, chief executive officer of GBM, stated: \"GBM has a 26-year heritage of partnering with leaders in the global IT industry. As we continue our transformation journey at GBM, our collaboration with Apple is a commitment to bringing further innovations to our customers, delivering more value to our stakeholders and enriching the market with a new set of solutions.\"\r\n\r\nHani Nofal, vice president of Intelligent Network Solutions, Security and Mobility at GBM, said: \"Adding Apple to our solutions portfolio strengthens our newly created mobility practice which is complemented by our strategic alliances with IBM and Cisco, who are both global technology partners of Apple too. We are excited about bringing joint mobility solutions from these global leaders to our region.\"'),
(20535, 887, '_about_partners_1_description', 'field_5b97a3674685c'),
