INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(34690, 1144, 'article_top_text', 'Founded and operating in the region since 1990, Gulf Business Machines (GBM) is the number one provider of IT solutions, leveraging industry leading technologies to drive real business results for clients.\r\n\r\nOriginally established through its partnership with IBM, the companies have continued to evolve their relationship in order to meet the ever increasing demands of the ICT market.'),
(34691, 1144, '_article_top_text', 'field_5b97f71cee263'),
(34692, 1144, 'article_left_text', 'With more than 28 years of experience, 7 offices and over 1440 employees across the region - Gulf Business Machines (GBM) is the region’s leading Hybrid Cloud & Digital Solutions provider.\r\n\r\nAs your end-to-end digital transformation partner, GBM offers the region’s broadest portfolio, including industry-leading infrastructure, digital business solutions, security and services.\r\n\r\nWe have nurtured partnerships with the world’s leading technology companies and invested in a talented, skilled workforce to implement solutions that cater to customer’s specific, complex and diverse business needs. With on-the ground, readily available expertise, GBM provides round-the-clock support to customers across the region.\r\n\r\n'),
(34693, 1144, '_article_left_text', 'field_5b97f731ee264'),
