(35384, 1147, 'persons_11_person_position', 'Director of Small and Medium Enterprises '),
(35385, 1147, '_persons_11_person_position', 'field_5b979cce342d9'),
(35386, 1147, 'persons_11_person_description', '<p dir=\"ltr\">The Director of Small &amp; Medium Business (SMB) is responsible for increasing GBM\'s market coverage and strengthening GBM\'s presence in the SME market. He is responsible for GBM\'s SMB strategy and execution and drives GBM\'s Go-To-Market, demand generation, offerings and solutions.</p>\r\n<p dir=\"ltr\">His business unit offers packaged offerings tailored for SMEs in the areas of Infrastructure Solutions, Business Solutions, Cloud Services and Solutions, Business Applications, Infrastructure Services and Managed Services, Retail Solutions, Endpoint Solutions and much more.</p>'),
(35387, 1147, '_persons_11_person_description', 'field_5b979cce343c7'),
